import React, { useState, useEffect } from 'react';
import { Modal, ModalBody } from 'reactstrap';
import axios from "axios";

const Main = () => {

  // DEFINIMOS EL STATE DONDE GURDAREMOS LA DATA
  const [data, setData] = useState([]);

  // DEFINIMOS EL STATE PARA MANIPULAR LA VENTANA MODAL
  const [modal, setModal]=useState(true);

  // SI MODALINSERTAR ES TRUE SE ABRE LA VENTANA MODAL 
  const abrirModal=()=>{
    setModal(!modal);
  }

  // PETICION AXIOS GET
  const peticionGet=()=>{
    axios.get("http://localhost:9000/tasks/all")
    .then(response=>{
      setData(response.data)
    })
    .catch((error)=> console.log(error))

  }

  useEffect(() => {

    peticionGet()
  }, [])

  return (
    <div className="App">
      <div className="container mt-3">
        <h1>TAREAS APP SENA</h1>
        <button className='btn btn-dark bt-sm'>Nueva</button>
        <div className="top mt-3">
          
        </div>      

        <div className="container-fluid">
          {/* OPERADOR CONDICIONAL TERNARIO DE JAVASCRIPT, RENDERIZARA SI ES VERDADERO => (?) Y SI NO => (:) */}
          {data.length > 0 ? 

            <table className="table table-striped">
                <thead>
                    <tr>
                        <th scope="col">#</th>
                        <th scope="col">Titulo</th>
                        <th scope="col">Estado</th>
                        <th scope="col">Acciones</th>
                    </tr>
                </thead>
                <tbody>
                  {data.map(task=>{
                    return(
                      <tr key={task._id}>
                        <td>{task._id}</td>
                        <th>{task.titulo}</th>
                        <th>{task.estado}</th>
                        <th>
                            <i className="fa-solid fa-trash"></i>
                            &nbsp; &nbsp; 
                            <i className="fa-solid fa-pen-to-square"></i>
                        </th>
                    </tr>
                    )
                  })}
                </tbody>
            </table>

          : <h1>No hay tareas</h1>}
          {/* SI NO SE CUMPLE LA CONDICION (NO ES VERDADERO), RENDERIZA EL H1 */}
            
        </div>

      </div>





      <Modal isOpen={abrirModal}>
        <ModalBody>
          <div className="form-group">
          <h3>AGREGAR EQUIPO</h3>
            <form>
              <br/>                         
                <label>Titulo</label>
                <input required className="form-control" type="text" name="titulo" />
              <br/>
                <label>Estado</label>
                <input required className="form-control" type="text" name="estado" />
              <br/>
              <div className='flex bg-white justify-content-start'>
                <button type='submit' className="btn btn-success m-1">Insertar</button>
                <button className="btn btn-danger m-1">Cancelar</button>
              </div>
            </form>
          </div>
        </ModalBody>
      </Modal>
    </div>
  )
}

export default Main;
