import React, { useState, useEffect } from 'react';
import axios from "axios";

const Main = () => {

  const [todo, setTodo] = useState([]);

  // PETICION AXIOS GET
  const peticionGet=()=>{
    axios.get("http://localhost:9000/tasks/all")
    .then(response=>{
      setTodo(response.data)
    })
    .catch((error)=> console.log(error))

  }

  useEffect(() => {

    peticionGet()
  }, [])

  return (
    <div className="App">
      <div className="container mt-3">
        <h1>TAREAS APP SENA</h1>
        <button className='btn btn-dark bt-sm'>Nueva</button>
        <div className="top mt-3">
          
        </div>      

        <div className="container-fluid">
          {/* OPERADOR CONDICIONAL TERNARIO DE JAVASCRIPT, RENDERIZARA SI ES VERDADERO => (?) Y SI NO => (:) */}
          {todo.length > 0 ? 

            <table className="table table-striped">
                <thead>
                    <tr>
                        <th scope="col">#</th>
                        <th scope="col">Titulo</th>
                        <th scope="col">Estado</th>
                        <th scope="col">Acciones</th>
                    </tr>
                </thead>
                <tbody>
                  {todo.map(task=>{
                    return(
                      <tr key={task._id}>
                        <th>{task._id}</th>
                        <td>{task.titulo}</td>
                        <td>{task.estado}</td>
                        <td>
                            <i className="fa-solid fa-trash"></i>
                            &nbsp; &nbsp; 
                            <i className="fa-solid fa-pen-to-square"></i>
                        </td>
                    </tr>
                    )
                  })}
                </tbody>
            </table>

          : <h1>No hay tareas</h1>}
          {/* SI NO SE CUMPLE LA CONDICION (NO ES VERDADERO), RENDERIZA EL H1 */}
            
        </div>

      </div>
    </div>
  )
}

export default Main
