import { useState, useEffect } from 'react';
import axios from "axios";

import './app.css';
import Item from './components/Item'

function App() {

  const [text, setText] = useState("");
  const [todo, setTodo] = useState([]);
  const [isUpdating, setUpdating] = useState("");

  // PETICIONES
  const peticionGet=()=>{
    axios.get("http://localhost:9000/tasks/all")
    .then(response=>{
      setTodo(response.data)
    })
    .catch((error)=> console.log(error))

  }

  useEffect(() => {

    peticionGet()
  }, [])

  console.log(todo);

  const addUpdateTodo = () => {

    if (isUpdating === "") {
      axios.post("http://localhost:9000/save-todo", { text })
        .then((res) => {
          console.log(res.data);
          setText("");
        })
        .catch((err) => console.log(err));
    }else{
      axios.post("http://localhost:9000/tasks/update/", { _id: isUpdating, text })
        .then((res) => {
          console.log(res.data);
          setText("");
          setUpdating("");
        })
        .catch((err) => console.log(err));
    }
  }

  const deleteTodo = (_id) => {
    axios.post("http://localhost:9000/tasks/delete/", { _id })
      .then((res) => console.log(res.data))
      .catch((err) => console.log(err));
  }

  const updateTodo = (_id, text) => {
    setUpdating(_id);
    setText(text);
  }

  return (
    <div className="App">
      <div className="container">
        <h1>Tareas App SENA</h1>
        <div className="top">
          <input
            type="text"
            placeholder='Write Something...'
            value={text}
            onChange={(e) => setText(e.target.value)} />
          <div className="add"
            onClick={addUpdateTodo}>{isUpdating ? "Update" : "Add"}</div>
        </div>      

        <div className="list">
          {todo.map(item => <Item
            key={item._id}
            titulo={item.titulo}
            remove={() => deleteTodo(item._id)}
            update={() => updateTodo(item._id, item.titulo)} />)}
        </div>

      </div>
    </div>
  );
}

export default App;
